mapper.select:
select * from ( select rowNum rnum, cate.categoryId, cate.categoryName, cate.parentName, cate.memberId, cate.regDate, cate.modifier, cate.modDate, cate.deleteYN from ( select child_cate.category_id categoryId, child_cate.name categoryName, parent_cate.name parentName, child_cate.member_id memberId, to_char(child_cate.reg_date,'yyyy/MM/dd') regDate, child_cate.modifier, to_char(child_cate.mod_date,'yyyy/MM/dd') modDate, child_cate.delete_yn deleteYN from category parent_cate, category child_cate where parent_cate.category_id = child_cate.parent_id
and child_cate.delete_yn = 'N'
and child_cate.delete_yn = 'Y'
and child_cate.parent_id = #{parentId}
and child_cate.parent_id is not null
and child_cate.name like '%'|| #{categoryName} || '%'
and to_char(child_cate.reg_date, 'yyyy-MM-dd') >= #{searchDate.startDate}
and to_char(child_cate.reg_date, 'yyyy-MM-dd') <= #{searchDate.endDate}
order by child_cate.category_id desc ) cate ) where rnum between ${pagination.start+1} and ${pagination.start + pagination.length}

select count(category_id) from category where 1=1
and delete_yn = 'N'
and delete_yn = 'Y'
and parent_id = #{parentId}
and parent_id is not null
and name like '%'|| #{categoryName} || '%'
and to_char(reg_date, 'yyyy-MM-dd') >= #{searchDate.startDate}
and to_char(reg_date, 'yyyy-MM-dd') <= #{searchDate.endDate}

select count(category_id) from category where parent_id is null
and delete_yn = 'N'
and delete_yn = 'Y'
and name like '%'|| #{categoryName} || '%'
and to_char(reg_date, 'yyyy-MM-dd') >= #{searchDate.startDate}
and to_char(reg_date, 'yyyy-MM-dd') <= #{searchDate.endDate}

select * from ( select rowNum rnum, cate.categoryId, cate.categoryName, cate.memberId, cate.regDate, cate.modifier, cate.modDate, cate.deleteYN from ( select category_id categoryId, name categoryName, member_id memberId, to_char(reg_date,'yyyy/MM/dd') regDate, modifier, to_char(mod_date, 'yyyy/MM/dd') modDate, delete_yn deleteYN from category where parent_id is null
and delete_yn = 'N'
and delete_yn = 'Y'
and name like '%'|| #{categoryName} || '%'
and to_char(reg_date, 'yyyy-MM-dd') >= #{searchDate.startDate}
and to_char(reg_date, 'yyyy-MM-dd') <= #{searchDate.endDate}
order by category_id desc ) cate ) where rnum between ${pagination.start+1} and ${pagination.start + pagination.length}
